/* Private define ------------------------------------------------------------*/
/* USER CODE BEGIN PD */
//semafor kimliği ve Semaforu kullanacak olan işlem ID’si
#define HSEM_ID 9
#define HSEM_PROCESS 4
/* USER CODE END PD */

/* Private macro -------------------------------------------------------------*/
/* USER CODE BEGIN PM */

/* USER CODE END PM */

/* Private variables ---------------------------------------------------------*/
UART_HandleTypeDef huart2;

/* USER CODE BEGIN PV */
//Semaforun durumunu saklar. Varsayılan olarak 0 (boşta) olarak başlatılmıştır.
uint8_t semaphore_status = 0;
/* USER CODE END PV */

/* Private function prototypes -----------------------------------------------*/
void SystemClock_Config(void);
static void MX_GPIO_Init(void);
static void MX_USART2_UART_Init(void);
/* USER CODE BEGIN PFP */

