//Eğer serbest bırakılan semafor HSEM_ID ile eşleşirse,
//semaphore_status değişkeni 1 yapılır.
void HAL_HSEM_FreeCallback(uint32_t SemMask)
{
	if((SemMask & __HAL_HSEM_SEMID_TO_MASK(HSEM_ID)) !=0 )

	semaphore_status = 1;
}
